let models = require('sql-models');


let FunctionList = {

  async createNewActivity(object){
      return await models.db.activity.create(object);
  },
  async createNewActivityLog(object){
      return await models.db.activity_log.create(object);
  },

  async findDirectManager(object){
    return await models.db.employees.findOne({
      where: {user_id: object},
      attributes: ['dm_id']
    })

  },
  async getActivity(object){
    return await models.db.activity.findOne({
      where: {activity_id: object},
      attributes: ['feed_data']
    })

  },


};

exports.fun = FunctionList;
