/**
 * Represents a peron.
 * @exports - Person Model for the "person" table
 * @fields {string} columns - The attribute of the person.
 * @key {array} keys - The primary key of the column-family/table.
 */
module.exports = {
    fields: {
        name: "text",
        surname: "text",
        age: "int"
    },
    key: ["name"],
    table_name: "person"
}


