var logger = {};

const LEVEL_DEBUG = 0;
const LEVEL_LOG = 1;
const LEVEL_WARN = 2;
const LEVEL_ERROR = 3;
const LEVEL_DISABLE = 4;

logger.level = LEVEL_DEBUG;

logger.error = (msg) => {
    if (logger.level < LEVEL_DISABLE)
        console.log('[error] ' + msg);
}

logger.warn = (msg) => {
    if (logger.level < LEVEL_ERROR)
        console.log('[warn] ' + msg);
}

logger.log = (msg) => {
    if (logger.level < LEVEL_WARN)
        console.log('[log] ' + msg);
}

logger.debug = (msg) => {
    if (logger.level < LEVEL_LOG)
        console.log('[debug] ' + msg);
}

module.exports = logger;
