let BaseModel = require('../base/Base');
let Schema    = require('../utils/Schema');


let AuthUserCredentials = new Schema(BaseModel, {
    fields    : {
        key      : "text",  //user_name
        cred_id  : "timeuuid",
        is_active: "boolean",
        key_type : "text",
        password : "text",
        pin      : "text",
        user_id  : "timeuuid",
    },
    key       : [["key"]],
    table_name: "auth_user_credentials",
});

module.exports = AuthUserCredentials.model();
