let BaseModel = require('../base/Base');
let Schema    = require('../utils/Schema');


let AuthUsers = new Schema(BaseModel, {
    fields    : {
        user_id       : "timeuuid",
        display_name  : "text", //ok
        display_pic   : "text",
        is_active     : "boolean", //ok
        mobile        : "bigint",//ok
        mobile2       : "bigint",//ok
        personal_email: "text",//ok
        user_name     : "text",//ok
        work_email    : "text"//ok
    },
    key       : [["user_id"]],
    indexes   : ["mobile", "mobile2", "personal_email", "user_name", "work_email"],
    table_name: "auth_users",
});

module.exports = AuthUsers.model();
