let Schema = require('../utils/Schema');
let Base   = require('./Base');

let TimeStampBase = new Schema(Base, {
    fields: {
        created_by: "timeuuid",
        created_on: "timestamp",
        deleted_by: "timeuuid",
        deleted_on: "timestamp",
        updated_by: "timeuuid",
        updated_on: "timestamp"
    }
});

module.exports = TimeStampBase.model();
