let TimeStampBase = require('../base/TimeStampBase');
let Schema        = require('../utils/Schema');

let EmployeesDetails = new Schema(TimeStampBase, {
    fields    : {
        le_id        : "timeuuid",
        emp_id       : "timeuuid",
        basic_details: {
            type   : "map",
            typeDef: "<text, text>"
        },
        extra_details: {
            type   : "map",
            typeDef: "<text, text>"
        }
    },
    key       : [["le_id"], "emp_id"],
    table_name: "emp_details"
});

module.exports = EmployeesDetails.model();
