let TimeStampBase = require('../base/TimeStampBase');
let Schema        = require('../utils/Schema');

let EmployeesPayrollInfo = new Schema(TimeStampBase, {
    fields    : {
        le_id   : "timeuuid",
        emp_id  : "timeuuid",
        dm      : "timeuuid",
        emp_code: "text",
        fm      : {
            type   : "set",
            typeDef: "<timeuuid>"
        },
        org_id  : "timeuuid",
        pos_id  : {
            type   : "set",
            typeDef: "<timeuuid>"
        },
    },
    key       : [["le_id"], "emp_id"],
    table_name: "emp_payroll_info"
});

module.exports = EmployeesPayrollInfo.model();
