let TimeStampBase = require('../base/TimeStampBase');
let Schema        = require('../utils/Schema');


let EmployeesSearch = new Schema(TimeStampBase, {
    fields        : {
        is_active: "boolean",
        property : "text",
        pvalue   : "text"
    },
    key           : [["is_active", "property"], "pvalue"],
    custom_indexes: [{
        on     : "pvalue",
        using  : "org.apache.cassandra.index.sasi.SASIIndex",
        options: {
            'analyzer_class': 'org.apache.cassandra.index.sasi.analyzer.NonTokenizingAnalyzer',
            'case_sensitive': 'false',
            'mode'          : 'CONTAINS'
        }
    }],
    table_name    : "emp_search"
});

module.exports = EmployeesSearch.model();
