let TimeStampBase = require('../base/TimeStampBase');
let Schema        = require('../utils/Schema');

let OrgBusinessUnits = new Schema(TimeStampBase, {
    fields    : {
        le_id      : "timeuuid",
        bu_id      : "timeuuid",
        bu_name    : "text",
        description: "text",
        head_pos_id: "timeuuid",
        is_active  : "boolean",
        org_code   : "text",
        org_id     : "timeuuid",
        type_id    : "timeuuid",
    },
    key       : [["le_id"], "bu_id"],
    table_name: "org_business_units"
});

module.exports = OrgBusinessUnits.model();


