let TimeStampBase = require('../base/TimeStampBase');
let Schema        = require('../utils/Schema');

let OrgFeedUnits = new Schema(TimeStampBase, {
    fields    : {
        org_id   : "timeuuid",
        user_id  : "timeuuid",
        day      : "date",
        feed_id  : "timeuuid",
        content  : "text",
        feed_type: "text",
        time     : "time",
    },
    key       : [["org_id", "user_id"], "day", "feed_id"],
    table_name: "org_feed"
});

module.exports = OrgFeedUnits.model();


