let TimeStampBase = require('../base/TimeStampBase');
let Schema        = require('../utils/Schema');

let OrgLegalEntities = new Schema(TimeStampBase, {
    fields    : {
        org_id     : "timeuuid",
        le_id      : "timeuuid",
        description: "text",
        is_active  : "boolean",
        le_address : {
            type   : "frozen",
            typeDef: "<address>"
        },
        le_name    : "text",
        logo_file  : "text",
        org_code   : "text"
    },
    key       : [["org_id"], "le_id"],
    table_name: "org_legal_entities"
});

module.exports = OrgLegalEntities.model();


