let TimeStampBase = require('../base/TimeStampBase');
let Schema        = require('../utils/Schema');

let OrgOrganizations = new Schema(TimeStampBase, {
    fields    : {
        org_id     : "timeuuid",
        org_code   : "text",
        description: "text",
        is_active  : "boolean",
        logo_file  : "text",
        org_address: {
            type   : "frozen",
            typeDef: "<address>"
        },
        org_name   : "text",
    },
    key       : [["org_id"], "org_code"],
    table_name: "org_organizations"
});

module.exports = OrgOrganizations.model();


