let TimeStampBase = require('../base/TimeStampBase');
let Schema        = require('../utils/Schema');

let OrgBusinessUnits = new Schema(TimeStampBase, {
    fields    : {
        le_id        : "timeuuid",
        pos_id       : "timeuuid",
        bu_id        : "timeuuid",
        description  : "text",
        designation  : "text",
        is_active    : "boolean",
        org_code     : "text",
        org_id       : "timeuuid",
        parent_pos_id: "timeuuid",
        pos_name     : "text",
    },
    key       : [["le_id"], "pos_id"],
    table_name: "org_positions"
});

module.exports = OrgBusinessUnits.model();


