let _     = require('lodash');
let merge = require('deepmerge');

class Schema {
    constructor(baseModel, newModel = {}) {
        if (baseModel instanceof Schema) {
            this.object = merge(baseModel.object, newModel);
        } else {
            this.object = merge(baseModel, newModel);
        }
    }

    toString() {
        return this.object;
    }

    model() {
        // console.log("this.object => ", this.object);
        return this.object;
    }
}

module.exports = Schema;
