module.exports.addRules = (nval) => {

    // server url
    nval.addRule("url", "url");

    // set stack trace on apis response or not
    nval.addRule("hideStack", [true, false]);

    //Socket config
    nval.addRule("socket", Object);
    nval.addRule("socket:port", "port");

    //database
    nval.addRule("database", Object);
    //application mongo
    nval.addRule("database:mongo", Object);
    nval.addRule("database:mongo:host", String);
    nval.addRule("database:mongo:schema", String);
    nval.addRule("database:mongo:logging", Boolean);
    // nval.addRule("database:mongo:user", Object);
    // nval.addRule("database:mongo:password", Object);


    //redis
    // nval.addRule("aws:redis:user_redis", Boolean);
    // nval.addRule("aws:redis:host", String);

    //aws
    nval.addRule("aws:s3:accessKeyId", String);
    nval.addRule("aws:s3:secretAccessKey", String);
    nval.addRule("aws:s3:region", String);
    nval.addRule("aws:s3:bucket_name", String);


    nval.addRule("sendCrashMail", Boolean);
    nval.addRule("crash_mails_ids", Array);

    //elastic
    nval.addRule("elastic", Object);

};

