/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('emp_codes', {
    emp_code: {
      type: DataTypes.STRING(45),
      allowNull: false,
      primaryKey: true
    },
    le_code: {
      type: DataTypes.STRING(45),
      allowNull: false
    },
    uid: {
      type: DataTypes.INTEGER(11),
      allowNull: false
    },
    olr_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    }
  }, {
    tableName: 'emp_codes'
  });
};
