/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('forms_data', {
    id: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    form_code: {
      type: DataTypes.STRING(45),
      allowNull: false
    },
    form_data: {
      type: DataTypes.JSON,
      allowNull: true
    }
  }, {
    tableName: 'forms_data'
  });
};
