/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('master', {
    id: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    csid: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'csid',
        key: 'id'
      }
    },
    code: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    title: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    table_name: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    generic: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    order: {
      type: DataTypes.STRING(45),
      allowNull: true
    }
  }, {
    tableName: 'master'
  });
};
