/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('master_data', {
    id: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    header_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'master_headers',
        key: 'id'
      }
    },
    row_id: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    value: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    is_active: {
      type: DataTypes.STRING(45),
      allowNull: true
    }
  }, {
    tableName: 'master_data'
  });
};
