/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('master_headers', {
    id: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    master_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'master',
        key: 'id'
      }
    },
    code: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    title: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    header_type: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    typeMasterId: {
      type: DataTypes.INTEGER(11),
      allowNull: true,
      references: {
        model: 'master',
        key: 'id'
      }
    },
    typeMasterHeaderId: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    }
  }, {
    tableName: 'master_headers'
  });
};
