/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('vw_emp_export', {
    Employee_Code: {
      type: DataTypes.STRING(200),
      allowNull: true
    },
    Employee_name: {
      type: DataTypes.STRING(201),
      allowNull: true
    },
    DOJ: {
      type: DataTypes.STRING(38),
      allowNull: true
    },
    Designation: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    Agency: {
      type: DataTypes.STRING(250),
      allowNull: true
    },
    Center_Name: {
      type: DataTypes.STRING(250),
      allowNull: true
    },
    location: {
      type: DataTypes.STRING(100),
      allowNull: true
    },
    Contact_No: {
      type: DataTypes.STRING(15),
      allowNull: true
    },
    Last_Working_Date: {
      type: DataTypes.STRING(38),
      allowNull: true
    }
  }, {
    tableName: 'vw_emp_export'
  });
};
