/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('wx_auth_data_types', {
    id: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    data_type: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    table: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    default_value: {
      type: DataTypes.STRING(45),
      allowNull: true
    }
  }, {
    tableName: 'wx_auth_data_types'
  });
};
