/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('wx_auth_role_assignment', {
    id: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    role_code: {
      type: DataTypes.STRING(45),
      allowNull: false
    },
    pos_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    emp_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    user_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    }
  }, {
    tableName: 'wx_auth_role_assignment'
  });
};
