/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('wx_auth_roles', {
    role_code: {
      type: DataTypes.STRING(45),
      allowNull: false,
      primaryKey: true
    },
    role_title: {
      type: DataTypes.STRING(100),
      allowNull: false
    },
    is_deleted: {
      type: DataTypes.BOOLEAN,
      allowNull: false,
      defaultValue: '0'
    },
    role_desc: {
      type: DataTypes.TEXT,
      allowNull: true
    }
  }, {
    tableName: 'wx_auth_roles'
  });
};
