/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('wx_dataTypes_roles', {
    id: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    role_code: {
      type: DataTypes.STRING(45),
      allowNull: false
    },
    emp_id: {
      type: DataTypes.STRING(45),
      allowNull: true
    },
    user_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    pos_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    data_type: {
      type: DataTypes.STRING(45),
      allowNull: false
    },
    option: {
      type: DataTypes.STRING(45),
      allowNull: false
    },
    value: {
      type: DataTypes.STRING(45),
      allowNull: true
    }
  }, {
    tableName: 'wx_dataTypes_roles'
  });
};
