/* jshint indent: 2 */

module.exports = function(sequelize, DataTypes) {
  return sequelize.define('wx_role_module_assignment', {
    id: {
      type: DataTypes.INTEGER(11),
      allowNull: false,
      primaryKey: true,
      autoIncrement: true
    },
    role_code: {
      type: DataTypes.STRING(45),
      allowNull: false
    },
    module_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    },
    sub_module_id: {
      type: DataTypes.INTEGER(11),
      allowNull: true
    }
  }, {
    tableName: 'wx_role_module_assignment'
  });
};
