let {WxComponents}     =  require('../constants');
let BaseComponent      = require('./base/BaseComponent');

class ActionsItem extends BaseComponent {

    static init(param) {
        return new ActionsItem(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.ACTION_ITEM};
    }

    setSubTitle(subTitle) {
        this.config.subtitle = subTitle;
        return this;
    }

    setNotificationCount(notificationCount) {
        this.config.notificationCount = notificationCount;
        return this;
    }

    setTextColor(textColor) {
        this.config.textColor = textColor;
        return this;
    }

    setBackgroundColor(backgroundColor) {
        this.config.backgroundColor = backgroundColor;
        return this;
    }
}

module.exports = ActionsItem;
