let {WxComponents, WebHookConstants} =  require('../constants');
let BaseGroupComponent               = require('./base/BaseGroupComponent');

class AutoComplete extends BaseGroupComponent {

    static init(param) {
        return new AutoComplete(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.AUTO_COMPLETE};
    }

    setWidthFull() {
        this.config.full = true;
        return this;
    }

}

module.exports = AutoComplete;
