let {WxComponents}     =  require('../constants');
let BaseGroupComponent = require('./base/BaseGroupComponent');

class CardStack extends BaseGroupComponent {

    static  init(param) {
        return new CardStack(param)
    }

    constructor(param) {
        super(param);
        this.config = {
            'type': WxComponents.CARD_STACK
        };
    }

    setSubTitle(subTitle) {
        this.config.subTitle = subTitle;
        return this;
    }

    setAvatar(avatar) {
        this.config.avatar = avatar;
        return this;
    }

    setPaging(currentPage, pageSize, totalPages, serverSide = true) {
        this.config.paging = {
            serverSide,
            currentPage,
            pageSize,
            totalPages
        };
        return this;
    }

}

module.exports = CardStack;
