let {WxComponents, WebHookConstants} =  require('../constants');
let BaseComponent = require('./base/BaseComponent');

class CheckBox extends BaseComponent {

    static init(param) {
        return new CheckBox(param);
    }

    setValidators(validators) {
        this.config.validators = validators;
        return this;
    }

    setErrorMessages(errorMessages) {
        this.config.errorMessages = errorMessages;
        return this;
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.CHECKBOX};
    }

    setChecked() {
        this.config.checked = true;
        this.config.value = true;
        return this;
    }

    setLabelPositionLeft() {
        this.config.labelPosition = 'left';
        return this;
    }

    setLabelPositionRight() {
        this.config.labelPosition = 'right';
        return this;
    }

}

module.exports = CheckBox;
