let {WxComponents} =  require('../constants');
let BaseComponent  = require('./base/BaseComponent');

class DataPicker extends BaseComponent {

    static  init(param) {
        return new DataPicker(param)
    }

    constructor(param) {
        super(param);
        this.config = {
            'type': WxComponents.DATE
        };
    }

    //Valid js date object
    setDefaultDate(defaultDate) {
        this.config.defaultValue = defaultDate;
        return this;
    }

    setDateFormat(dateFormat) {
        console.log("setDateFormat => is not working in current version. ")
        this.config.dateFormat = dateFormat;
        return this;
    }

}

module.exports = DataPicker;
