let {WxComponents}     =  require('../constants');
let BaseGroupComponent = require('./base/BaseGroupComponent');

class DropDown extends BaseGroupComponent {

    static init(param) {
        return new DropDown(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.DROP_DOWN};
    }

    setWidthFull() {
        this.config.full = true;
        return this;
    }

}

module.exports = DropDown;
