let {WxComponents, WebHookConstants} =  require('../constants');
let BaseContainer                    = require('./base/BaseContainer');
let map                              = require('lodash/map');

class FetchContainer extends BaseContainer {

    static init(param) {
        return new FetchContainer(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.FETCH_CONTAINER};
    }

    setWebhook(url, method = WebHookConstants.GET_METHOD, header, body) {
        this.config.webhook = {
            url, method, header, body
        };
        return this;
    }
}

module.exports = FetchContainer;
