let {WxComponents, WebHookConstants} =  require('../constants');
let BaseComponent                    = require('./base/BaseComponent');

class Icon extends BaseComponent {

    static init(param) {
        return new Icon(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.ICON};
    }

    setIconColor(iconColor) {
        this.config.iconColor = iconColor;
        return this;
    }

    setIcon(icon) {
        this.config.icon = icon;
        return this;
    }
}

module.exports = Icon;
