let {WxComponents, WebHookConstants} =  require('../constants');
let BaseComponent = require('./base/BaseComponent');

class ImagePreviewUploader extends BaseComponent {

    static init(param) {
        return new ImagePreviewUploader(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.IMAGE_PREVIEW_UPLOADER};
    }

    setUrl(url) {
        this.config.url = url;
        return this;
    }
}

module.exports = ImagePreviewUploader;
