let {WxComponents, WebHookConstants} = require('../constants');
let BaseComponent                    = require('./base/BaseComponent');
let chalk                            = require('chalk');

class Link extends BaseComponent {

    static init(param) {
        return new Link(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.LINK};
    }

    setUrl(url) {
        this.config.url = url;
        return this;
    }

    setTarget(target) {
        if (target === '_blank' || target === '_self' || target === '_parent' || target === '_top')
            this.config.target = target;
        else
            console.log(chalk.red('In valid target supplied to Link\n' +
                '_blank , _self , _parent , _ top are only supported'));
        return this;
    }
}

module.exports = Link;
