let {WxComponents} =  require('../constants');
let BaseContainer  = require('./base/BaseContainer');

class ListItem extends BaseContainer {

    static init(param) {
        return new ListItem(param);
    }

    constructor(param) {
        super(param);
        this.config = {
            'type': WxComponents.LIST_ITEM
        }
    }

    setLeftIcon(icon) {
        this.config.leftIcon = icon;
        return this;
    }

    setLeftIconColor(iconColor) {
        this.config.leftIconColor = iconColor;
        return this;
    }

}

module.exports = ListItem;
