let {WxComponents, WebHookConstants} =  require('../constants');
let BaseGroupComponent               = require('./base/BaseGroupComponent');

class MultiSelect extends BaseGroupComponent {

    static init(param) {
        return new MultiSelect(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.MULTI_SELECT};
    }

    setInline() {
        this.config.inline = true;
        return this;
    }
}

module.exports = MultiSelect;
