let {WxComponents, WebHookConstants} =  require('../constants');
let BaseGroupComponent = require('./base/BaseGroupComponent');

class MultiSelectDropDown extends BaseGroupComponent {

    static init(param) {
        return new MultiSelectDropDown(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.MULTI_SELECT_DROP_DOWN};
    }

    setInline() {
        this.config.inline = true;
        return this;
    }
}

module.exports = MultiSelectDropDown;
