let {WxComponents, WebHookConstants} =  require('../constants');
let BaseContainer                    = require('./base/BaseContainer');
let map                              = require('lodash/map');

class Page extends BaseContainer {

    static init(param) {
        return new Page(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.PAGE, fields: []};
    }

    setCode(code) {
        this.config.code = code;
        return this;
    }

    setHeader(headerContent) {
        this.config.header = headerContent.render();
        return this;
    }
}

module.exports = Page;
