let {WxComponents, WebHookConstants} =  require('../constants');
let BaseContainer                    = require('./base/BaseContainer');

class Paper extends BaseContainer {

    static init(param) {
        return new Paper(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.PAPER};
    }

    setDepth(depth) {
        this.config.depth = depth;
        return this;
    }

}

module.exports = Paper;
