let {WxComponents, WebHookConstants} =  require('../constants');
let BaseGroupComponent               = require('./base/BaseGroupComponent');


class RadioButtonGroup extends BaseGroupComponent {

    static init(param) {
        return new RadioButtonGroup(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.RADIO};
    }

    setInline() {
        this.config.inline = true;
        return this;
    }

}

module.exports = RadioButtonGroup;
