let {WxComponents} =  require('../constants');
let BaseContainer  = require('./base/BaseContainer');
let map            = require('lodash/map');
const chalk    = require('chalk');

class Section extends BaseContainer {

    static init(param) {
        return new Section(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.SECTION};
    }

    setSubTitle(subTitle) {
        this.config.subTitle = subTitle;
        return this;
    }

    setAvatar(avatar) {
        this.config.avatar = avatar;
        return this;
    }

    makeExpandable() {
        this.config.isExpandable = true;
        return this;
    }

    isExpandable() {
        console.log(chalk.red("isExpandable is deprecated, instead use makeExpandable "));
        this.config.isExpandable = true;
        return this;
    }

    setShadowClass(className) {
        this.config.shadowClass = className;
        return this;
    }
}

module.exports = Section;
