let {WxComponents} =  require('../constants');
let BaseComponent  = require('./base/BaseComponent');
let map            = require('lodash/map');
let chalk          = require('chalk');

class Span extends BaseComponent {

    static init(param) {
        return new Span(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.SPAN};
    }

    setText(text) {
        console.log(chalk.red('Don\'t use \'setText\' on Span instead use \'setTitle\''));
        this.config.text = text;
        return this;
    }

    setSize(size) {
        this.config.size = size;
        return this;
    }
}

module.exports = Span;
