let {WxComponents} =  require('../constants');
let BaseContainer  = require('./base/BaseContainer');

class Tab extends BaseContainer {

    static init(param) {
        return new Tab(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.TAB};
    }

    setIconColor(iconColor) {
        this.config.iconColor = iconColor;
        return this;
    }

    setIcon(icon) {
        this.config.icon = icon;
        return this;
    }
}

module.exports = Tab;
