let {WxComponents} =  require('../constants');
let BaseComponent  = require('./base/BaseComponent');

class TextArea extends BaseComponent {

    static init(param) {
        return new TextArea(param);
    }

    constructor(params) {
        super(params);
        this.config = {
            'type': WxComponents.TEXT_AREA
        }
    }

    setTypePassword() {
        this.config.input_type = 'password';
        return this;
    }

    setWidthFull() {
        this.config.full = true;
        return this;
    }

    setHint(hint) {
        this.config.hint = hint;
        return this;
    }

    setTitle(title) {
        this.config.title = title;
        return this;
    }

    setRows(rows) {
        this.config.rows = rows;
        return this;
    }

    setDefaultValue(defaultValue) {
        this.config.defaultValue = defaultValue;
        return this;
    }
}

module.exports = TextArea;



