let {WxComponents} =  require('../constants');
let BaseComponent  = require('./base/BaseComponent');

class TimePicker extends BaseComponent {

    static init(param) {
        return new TimePicker(param)
    }

    constructor(param) {
        super(param);
        this.config = {
            'type': WxComponents.TIME
        };
    }

    setDefaultTime(defaultTime) {
        console.log("setDefaultTime is not implemented on client side => ");
        this.config.defaultTime = defaultTime;
        return this;
    }

    set24HrsFormat(dateFormat) {
        this.config.format = '24hr';
        return this;
    }

    setAmPmFormat(dateFormat) {
        this.config.format = 'ampm';
        return this;
    }

}

module.exports = TimePicker;
