let {WxComponents} =  require('../constants');
let BaseComponent  = require('./base/BaseComponent');

class Toggle extends BaseComponent {

    static init(param) {
        return new Toggle(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.TOGGLE};
    }

    setChecked() {
        this.config.checked = true;
        return this;
    }

    setLabelPositionLeft() {
        this.config.labelPosition = 'left';
        return this;
    }

    setLabelPositionRight() {
        this.config.labelPosition = 'right';
        return this;
    }

}

module.exports = Toggle;
