let {WxComponents} =  require('../constants');
let BaseContainer  = require('./base/BaseContainer');

class VerticalStack extends BaseContainer {

    static init(param) {
        return new VerticalStack(param);
    }

    constructor(param) {
        super(param);
        this.config = {'type': WxComponents.VERTICAL_STACK};
    }

    setRowSpacing(rowSpacing){
        this.config.rowSpacing = rowSpacing;
        return this;
    }
}

module.exports = VerticalStack;
